settings.css:
div[id*="root"] {
	font-size: 100px;
}

img[class*="voice_avatar"] {
	object-fit: cover;
	width: 150px;
	height: 75px;
	border: 0;
	border-radius:0;
	filter:brightness(50%);
	transition: all 0.2s ease-in-out;
}

img[class*="avatarSpeaking"] {
 filter:brightness(95%); 
}

ul[class*="voice_states"] {
	width: 1800px;
	padding-left: 0px;
	margin-top: 0px;
	display: flex;
	flex-direction:row;
	align-items:center;
	justify-content:center;
}

li[class*="voice_state"] {
	display: flex;
	flex-direction: column;
	margin-bottom:50px;
}

div[class*="voice_username"] {
	text-align: center;
	padding-top: 0;
}

span[class*="name"] {
	display: block;
	width:140px;
	padding-inline: 5px;
	padding-block: 3px;
	background-color: rgba(0,0,0,0.7) !important;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
}

li[data-userid*=""] {display: none !important;}

.self_mute, .self_deaf {
    display: none !important;
}
